package cc.wangzijie.server.utils;import org.springframework.boot.jdbc.DataSourceBuilder;import org.sqlite.JDBC;import javax.sql.DataSource;public class SqliteBuilder {    private String filePath;    private String url;    public static SqliteBuilder create(){        return  new SqliteBuilder();    }    public SqliteBuilder filePath(String filePath){        this.filePath =filePath;        return this;    }    public SqliteBuilder url(String url) {        this.url = url;        return this;    }    public DataSource build() {        if (url != null && !url.isEmpty()) {            return DataSourceBuilder.create().                    url(url).driverClassName(JDBC.class.getName()).build();        }        if (filePath != null && !filePath.isEmpty()) {            url = buildUrl(filePath);            return DataSourceBuilder.create().                    url(url).driverClassName(JDBC.class.getName()).build();        }        return DataSourceBuilder.create().build();    }    public static String parseFilePath(String url){        return url.replace("jdbc:sqlite:", "");    }    public static String buildUrl(String filePath){        return "jdbc:sqlite:" + filePath;    }}